package com.relesi.smart.api.services.impl;import com.relesi.smart.api.entities.Empresa;import com.relesi.smart.api.repositories.EmpresaRepository;import com.relesi.smart.api.services.EmpresaService;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.Optional;@Servicepublic class EmpresaServiceImpl implements EmpresaService {    private static final Logger log = (Logger) LoggerFactory.getLogger(EmpresaServiceImpl.class);    @Autowired    private EmpresaRepository empresaRepository;    @Override    public Optional<Empresa> buscarPorCnpj(String cnpj) {        log.info("Buscando uma empresa para o CNPJ {}", cnpj);        return Optional.ofNullable(empresaRepository.findByCnpj(cnpj));    }    @Override    public Empresa persistir(Empresa empresa) {        log.info("Persisitindo empresa: {} ", empresa);        return this.empresaRepository.save(empresa);    }}