package com.relesi.smart.api.utils;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.security.crypto.bcrypt.BCryptPasswordEncoder;public class PasswordUtils {    private static final Logger log = LoggerFactory.getLogger(PasswordUtils.class);    public PasswordUtils(){    }    /**     * Gerar um hash utilizando o BCript     *     * @param     * @return String     */    public static String gerarBCrypt(String senha){        if (senha == null){            return senha;        }        log.info("Gerando hash com BCrypt");        BCryptPasswordEncoder byCryptEncoder = new BCryptPasswordEncoder();        return byCryptEncoder.encode(senha);    }}